import React from 'react';

export class PlanetList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           planets: []
        }
     }

   componentDidMount() {
      //fetch('https://swapi.co/api/planets/').then(({ results }) => this.setState({ planets: results }));
      fetch('https://swapi.co/api/planets/?format=json').then(
         response => {
            this.setState({ planets: response.results });
         }
      )
   }

   //  UserList() {
   //    // $.getJSON('https://swapi.co/api/planets/')
   //    //   .then(({ results }) => this.setState({ data: results }));
   //    fetch('https://swapi.co/api/planets/')
   //    .then(({ results }) => this.setState({ data: results }));

   //  }
  
     render() {
      const { planets } = this.state;
        return (
           <div>
              <h1>List of Planets</h1>
              <table>
                 <tbody>
                     {planets ? planets.map((planet, i) => <TableRow key = {i} 
                     data = {planet} />) : "Its cloud"}
                 </tbody>
              </table>
           </div>
        );
     }
  
}

class TableRow extends React.Component {
   render() {
      return (
         <tr>
            <td>{this.props.data.id}</td>
            <td>{this.props.data.name}</td>
            <td>{this.props.data.age}</td>
         </tr>
      );
   }
}
