import React from 'react';

export class PlanetList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           next:"",
           prev:"",
           planets: [],
           planetDetails: {}
        }
        this.loadOtherPlanets = this.loadOtherPlanets.bind(this);
        this.loadPlanetDetails = this.loadPlanetDetails.bind(this);
     };

   componentDidMount() {
      this.loadOtherPlanets(null);
   };

   loadOtherPlanets(apiUrl) {
      //alert("Hi");
      if(apiUrl == null) { 
         apiUrl = 'https://swapi.co/api/planets/';
      }
      fetch(apiUrl).then(res => res.json()).then(
         (response) => {
            this.setState({ next: response.next, prev:response.previous, planets: response.results });
         }
      )
   };

   loadPlanetDetails(apiUrl) {
      //alert("Hi");
      if(apiUrl) { 
         fetch(apiUrl).then(res => res.json()).then(
            (response) => {
               this.setState({ planetDetails: response });
            }
         )
      }
   };
     
     render() {
      const { planets } = this.state;
        return (
           <div>
              <h1>List of Planets</h1>
              <table>
                 <tbody>
                     {/* {planets ? planets.map((planet, i) => <TableRow key = {i} 
                     data = {planet} />) : "Its cloud"} */}
                     {planets ? planets.map((planet, i) => <tr>
                        <td><button className="" onClick={() => this.loadPlanetDetails(planet.url)}>{planet.name}</button></td>
                        </tr>) : "Its cloud"}
                 </tbody>
              </table>
              <button onClick={() => this.loadOtherPlanets(this.state.prev)}>Previous</button>
              <button onClick={() => this.loadOtherPlanets(this.state.next)}>Next</button>
           </div>
        );
     }
}

