import React from 'react';

export class PlanetList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
           next:"",
           prev:"",
           planets: []
        }
        //this.loadOtherPlanets = this.loadOtherPlanets.bind(this);
     };

   componentDidMount() {
      fetch('https://swapi.co/api/planets/').then(res => res.json()).then(
         (response) => {
            this.setState({ next: response.next, prev:response.previous, planets: response.results });
         }
      )
   };

   loadOtherPlanets(apiUrl) {
      if(apiUrl) {
         fetch(apiUrl).then(res => res.json()).then(
            (response) => {
               this.setState({ next: response.next, prev:response.previous, planets: response.results });
            }
         )
      }
     };
     
     render() {
      const { planets } = this.state;
        return (
           <div>
              <h1>List of Planets</h1>
              <table>
                 <tbody>
                     {planets ? planets.map((planet, i) => <TableRow key = {i} 
                     data = {planet} />) : "Its cloud"}
                 </tbody>
              </table>
              <button onClick={this.loadOtherPlanets(this.state.next)}>Next</button>
              <button onClick={this.loadOtherPlanets(this.state.prev)}>Previous</button>
           </div>
        );
     }

     

}

class TableRow extends React.Component {
   render() {
      return (
         <tr>
            <td>{this.props.data.id}</td>
            <td>{this.props.data.name}</td>
            <td>{this.props.data.age}</td>
         </tr>
      );
   }
}
